    {/* The temperature in Florence is: {response} °F */}


    if (response) {
      return Object.keys(response['horse']).map(function(key) {
        // const { id, name} = horse //destructuring
        return (
          <>
          <tr>
            <th>No</th>
            <th>Horse</th>
            <th>Time</th>
          </tr>
          <tr key={key}>
              <td>{response['horse'].id}</td>
              <td>{response['horse'].name}</td>
              <td>-</td>
              {/* <td>{time}</td> */}
          </tr>
          </>
        )
    })
    } else {
      return;
    }

  }
  render() {
    const { response } = this.state;
    return (
        <div style={{ textAlign: "center" }}>
          {response
              ? <table id='results'>
                <tbody>
                  {this.renderTableData()}
               </tbody>


              </table>
              : <p>Loading...</p>}
        </div>
    );
  }
}
export default App;


