import React, { Component } from "react";
import socketIOClient from "socket.io-client";



class App extends React.Component {
  constructor() {
    super();
    this.state = {
      response: false,
      endpoint: "http://127.0.0.1:4001",
      horses: null,
    };
    const horseList = {};
  }

  componentDidMount() {
    const { endpoint } = this.state;
    const socket = socketIOClient(endpoint);
    socket.on("FromAPI", data => this.setState({ response: data }));
    console.log('mounted!')
    console.log('state', this.state.response);
  }

  componentDidUpdate(prevState) {
    const resp = this.state.response;

    if (resp.event === 'start') {



      // this.state.horses += resp.horses;
      console.log('horseList', this.state.horses);

    }

    console.log('updating...');
    console.log('prevState', prevState);
    console.log('state', this.state.response);
    // If we have a new update
    // if (!this.featuresEqual(prevState.response, this.state.response)) {

    // }

  }

  featuresEqual(a, b) {
    const comparable = (features) => features
        .slice()
        .sort()
        .join(',');

    return comparable(a) === comparable(b);
  }

  renderTableData() {
    const { response } = this.state;

    // {Object.keys(response).map(function(key) {
    //   // TODO:
    //    if (typeof key === 'object') {}

    //   return <div key={key}> Key value is {key}, and Value is {response[key]} </div>
    // })}
    // {/* The temperature in Florence is: {response} °F */}


    if (this.state.horses) {
      return this.state.horses.map((horse, index) => {
        const { id, name, time} = horse //destructuring
        return (
          <tr key={id}>
              <td>{id}</td>
              <td>{name}</td>
              {/* <td>{time}</td> */}
          </tr>
        )
    })
    } else {
      return;
    }

  }
  render() {
    const { response } = this.state;
    return (
        <div style={{ textAlign: "center" }}>
          {response
              ? <table id='results'>
                <tbody>
                  {this.renderTableData()}
               </tbody>


              </table>
              : <p>Loading...</p>}
        </div>
    );
  }
}
export default App;


