import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import _ from 'underscore';


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      response: false,
      endpoint: "http://127.0.0.1:4001",
      horses: null,
    };
  }

  componentDidMount() {
    const { endpoint } = this.state;
    const socket = socketIOClient(endpoint);
    socket.on("FromAPI", data => this.setState({ response: data }));
    console.log('mounted!')
    console.log('state', this.state.response);
  }

  componentDidUpdate(prevState) {
    const resp = this.state.response;

    if (resp[0].event === 'start') {

      console.log('horseList', this.state.horses);

    }

    console.log('updating...');
    console.log('prevState', prevState);
    console.log('state', this.state.response);
    // If we have a new update
    // if (!this.featuresEqual(prevState.response, this.state.response)) {

    // }

  }

  featuresEqual(a, b) {
    const comparable = (features) => features
        .slice()
        .sort()
        .join(',');

    return comparable(a) === comparable(b);
  }

  renderTableData() {
    const { response } = this.state;
    console.log('response', response);

    // {Object.keys(response.horse).map(function(key) {
    //   // TODO:
    //    if (typeof key === 'object') {}

    //   return <div key={key}> Key value is {key}, and Value is {response[key]} </div>
    // })}
    {/* The temperature in Florence is: {response} °F */}


    if (response) {



      return Object.keys(_.sortBy(response, response.time).map(function(key) {
        return (
          <tr key={key}>
              <td>{response[key].horse.id}</td>
              <td>{response[key].horse.name}</td>
              <td>{response[key].time > 0 ? response[key].time : ''}</td>
          </tr>
        )
      }));
    } else {
      return;
    }

  }
  compare(a, b) {
    const timeA = a.time;
    const timeB = b.time;

    let comparison = 0;
    if (timeA > timeB) {
      comparison = 1;
    } else if (timeA < timeB) {
      comparison = -1;
    }
    return comparison;
  }
  render() {
    const { response } = this.state;
    return (
        <div style={{ textAlign: "center" }}>
          <table id='results'>
            <tbody>
              <tr>
                <th>No</th>
                <th>Horse</th>
                <th>Time</th>
              </tr>
              {response
                ? this.renderTableData()
                : <p>Loading...</p>}
            </tbody>
          </table>
        </div>
    );
  }
}
export default App;


